function preloadImages(srcs) {
    if (!preloadImages.cache) {
        preloadImages.cache = []
    }
    var img;
    for (var i = 0; i < srcs.length; i += 1) {
        img = new Image();
        img.src = srcs[i];
        preloadImages
            .cache
            .push(img)
    }
}
